script:
  function hideAll() {
    $("#meeting-info").hide();
    $("#connect-info").hide();
    $("#copy-2073152566").hide();
    $("#copy-2073152567").hide();
    $("#copy-2073152568").hide();
    $("#copy-2073152000").hide();
    $("#copy-2073152561").hide();
    $("#copy-2073152562").hide();
    $("#copy-2073152563").hide();
    $("#copy-2073152564").hide();
    $("#copy-2073152005").hide();
    $("#invitation").hide();
    $("#invitation-header").hide();
    $("#invalid-meeting-id").hide();
    $("#meeting-info").hide();
    $("#connect-info").hide();
    $("#invitation").hide();
    $("#invitation-header").hide();
    $("#zoom").hide();
    $("#faq").hide();
  }
  function hideFAQ() {
    $('#faq').hide();
  }
  function showFAQ() {
    hideAll();
    $('#faq').show();
  }

  $(document).ready(function() {
    $("#meeting-info").hide();
    $("#connect-info").hide();
    $("#copy-2073152566").hide();
    $("#copy-2073152567").hide();
    $("#copy-2073152568").hide();
    $("#copy-2073152000").hide();
    $("#copy-2073152561").hide();
    $("#copy-2073152562").hide();
    $("#copy-2073152563").hide();
    $("#copy-2073152564").hide();
    $("#copy-2073152005").hide();
    $("#invitation").hide();
    $("#invitation-header").hide();
    $("#invalid-meeting-id").hide();
    $('#faq').hide();


    $(window).trigger('hashchange')
  });

  $(window).on('hashchange', function(e){
    if(window.location.hash) {
      if($.isNumeric(window.location.hash.toString().substring(1)) && window.location.hash.toString().substring(1).length <=10 && window.location.hash.toString().substring(1).length >= 9) {
        $('#meeting-room').val(window.location.hash.toString().substring(1)).trigger('change');
      } else if (window.location.hash.toString().substring(1) == "faq") {
        showFAQ();
        $('#meeting-room').val('select');
      } else {
        $('#invalid-meeting-id').fadeIn(500).delay(5000).fadeOut(500);
      }
    } else {
      console.log("No hash");
    }
  });

  var meetingName = {2073152566: "Boadroom",2073152567: "Classroom",2073152568: "Alfond Room",2073152000: "Webinar",2073152561: "Pro One",2073152562: "Pro Two",2073152563: "Pro Three",2073152564: "Pro Four"};

  $('#meeting-room').change(function() {
    var meetingID = "";
    if (this.value == "") {
      $('#meeting-room option[value="other"]').text("Other (" + window.location.hash.toString().substring(1).replace(/(\d{3})(\d{3})(\d{4})/, '$1 $2 $3') + ")");
      $('#meeting-room').val('other');
      meetingID = window.location.hash.toString().substring(1);
    }
    else {
      meetingID = this.value

      window.location.hash = meetingID;
      $($(this).attr('href')).fadeIn('slow');
    }

    hideFAQ();

    var meeting_id_format = meetingID.replace(/(\d{3})(\d{3})(\d{4})/, '$1 $2 $3');
    $('#zoom-name').html(meetingName[meetingID]);
    $('#meeting-id').html(meeting_id_format);

    $('#call-link').html('
Call');
    $('#join-link').html('
Join now');
    $('#email-link').html('
Send email');
    $('#create-event').html('
Create event');
    $('#copy-invitation').html('Copied!Copy invitation ');
    $('#invitation-meeting-id').html(meetingID);
    $('#invitation-meeting-id-format').html(meeting_id_format);

    $('.meeting-id').html(meetingID);
    $("#meeting-info").show()
    $("#connect-info").show()
    $("#invitation").show()
    $("#invitation-header").show()
    $("#zoom").hide()
  });

  function copyToClipboard(id) {
    $('#copy-'+id).show();
    $('#copy-'+id).focus();
    $('#copy-'+id).select();
    document.execCommand('copy');
    $('#copy-'+id).hide();    
    $("#invitation-link-inside").fadeOut(500).delay(1500).fadeIn(0);
  }
